package entity.student;

public class Student {

	private int roll;
	private String name;
	private double marks;

	public Student(int r, String s, double m) {
		roll = r;
		name = s;
		marks = m;
	}

	public int getRoll() {
		// TODO Auto-generated method stub
		return roll;
	}

	public String getName() {
		// TODO Auto-generated method stub
		return name;
	}

	public double getMarks() {
		// TODO Auto-generated method stub
		return marks;
	}
}
